var React = require('react');
window.$ = window.jQuery = require('jquery');

var Bool = React.createClass({displayName: "Bool",

  componentDidMount: function () {
    $(this.refs.checkbox1.getDOMNode()).checkbox();
  },

  getValue: function () {
    return this.refs.input1.getDOMNode().checked;
  },

  render: function () {
    var checkbox = (
      React.createElement("div", {ref: "checkbox1", className: "checkbox"}, 
        React.createElement("label", {className: "checkbox-custom", "data-initialize": "checkbox"}, 
          React.createElement("input", {ref: "input1", type: "checkbox", name: this.props.field, id: this.props.field, defaultChecked: this.props.value, className: "sr-only"}), 
          React.createElement("span", {className: "checkbox-label"}, this.props.title)
        )
      )
    );
    if (this.props.noContainer) {
      return checkbox;
    }
    var labelGridColumns = this.props.labelGridColumns || 2;
    var controlGridColumns = 12 - labelGridColumns;
    if (this.props.formType === 'horizontal') {
      return (
        React.createElement("div", {className: "form-group"}, 
          React.createElement("div", {className: 'col-sm-offset-' + labelGridColumns + ' col-sm-' + controlGridColumns}, 
            checkbox
          )
        )
      );
    } else {
      return checkbox;
    }
  }
});

module.exports = Bool;