var React = require('react');
var mui = require('material-ui');
var Checkbox = mui.Checkbox;

var Bool = React.createClass({displayName: "Bool",

  render: function () {
    return (
      React.createElement("div", {className: "mm-checkbox-control"}, 
        React.createElement("div", {className: "mm-checkbox-label"}, this.props.title), 
        React.createElement(Checkbox, {type: "text", name: this.props.field})
      )
    );
  }
});

module.exports = Bool;